    [TestMethod]
    [DataRow(FirefoxCurlBash)]
    [DataRow(EdgeCurlBash)]
    public void Should_Build_Success(string command)
    {
        var request = RequestBuildTool.FromCurl(command);
        Assert.IsNotNull(request);
    }

    [TestMethod]
    [DataRow(FirefoxCurlBash)]
    [DataRow(EdgeCurlBash)]
    public void Should_Request_Success(string command)
    {
        var isSuccessStatusCode = RunAsync().GetAwaiter().GetResult();

        Assert.IsTrue(isSuccessStatusCode);

        GC.Collect();
        GC.WaitForPendingFinalizers();
        GC.WaitForFullGCComplete();

        async Task<bool> RunAsync()
        {
            var request = RequestBuildTool.FromCurl(command);
            var httpRst = await request.TryGetAsStringAsync();

            return httpRst.IsSuccessStatusCode;
        }
    }

    [TestInitialize]
    public void Init()
    {
        HttpRequestGlobalOptions.DefaultHttpMessageInvokerPool = new SimpleHttpMessageInvokerPool();
    }

